    <script>
        // loaded page event
        document.addEventListener('DOMContentLoaded', () => {
            const getProductsXX = () => {
                const products = []
                const dataProducts = localStorage.getItem('formulas_productos')

                // validating
                if (dataProducts) {
                    // iterating products
                    dataProducts.split('***').forEach(item => {
                        // add product to return
                        products.push(JSON.parse(item))
                    })
                }

                return products
            }

            const dataProductsXX	= getProductsXX()
            const $cartTable		= document.querySelector('.woocommerce-cart-form')

            console.log('dataProductsXX', dataProductsXX)

            if (dataProductsXX.length > 0 && !$cartTable) {
                console.log('reload')
                window.location.reload()
            }
        })
    </script>

